class Gallery{constructor(){this.currentImage=0,this.imageSet=document.getElementsByClassName('gallery-image'),this.totalSet=this.imageSet.length,this.left=document.getElementById('previous'),this.right=document.getElementById('next'),this.gallery=setInterval(()=>{if(this.currentImage===this.totalSet-1){this.change(0)}else if(0<=this.currentImage&&this.currentImage<this.totalSet-1){let a=this.currentImage+1;this.change(a)}},7e3)}removeClass(){this.current=document.getElementsByClassName('active-image')[0],this.current.classList.remove('active-image')}addClass(a){this.imageSet[a].classList.add('active-image'),this.currentImage=a}change(a){this.removeClass(),this.addClass(a)}forward(){if(clearInterval(this.gallery),this.currentImage===this.totalSet-1){this.change(0)}else if(0<=this.currentImage&&this.currentImage<this.totalSet-1){let a=this.currentImage+1;this.change(a)}}backward(){if(clearInterval(this.gallery),0===this.currentImage){let a=this.totalSet-1;this.change(a)}else if(0<this.currentImage){let a=this.currentImage-1;this.change(a)}}launch(){this.left.addEventListener('click',()=>{this.backward()}),this.right.addEventListener('click',()=>{this.forward()})}}let gallery=new Gallery;gallery.launch();
